SELECT aiod_entry.date_modified, dataset.identifier, name, description_identifier, text.plain as 'plain', text.html as 'html', issn
FROM aiod.dataset
INNER JOIN aiod.aiod_entry ON aiod.dataset.aiod_entry_identifier=aiod.aiod_entry.identifier
LEFT JOIN aiod.text ON aiod.dataset.description_identifier=aiod.text.identifier
WHERE aiod.dataset.date_deleted IS NULL
