SELECT experiment.identifier, experiment.name, text.plain as 'plain', text.html as 'html', aiod_entry.date_modified 
FROM aiod.experiment
INNER JOIN aiod.aiod_entry ON aiod.experiment.aiod_entry_identifier=aiod.aiod_entry.identifier
LEFT JOIN aiod.text ON aiod.experiment.description_identifier=aiod.text.identifier
WHERE aiod.experiment.date_deleted IS NULL AND aiod.aiod_entry.date_modified > :sql_last_value